<?phpclass Itech_Changetheme_IndexController extends Mage_Core_Controller_Front_Action {    	public function indexAction() {        $type = $this->getRequest()->getParam('type');		$url = $this->getRequest()->getParam('url');		if($type == 'fullsize'){			$theme = Mage::getStoreConfig('changetheme/general/full_theme');			if($theme!=''){				Mage::getSingleton('core/session')->setCustomizeTheme($theme);			}		}		if($type == 'mobile'){			$theme = Mage::getStoreConfig('changetheme/general/mobile_theme');			if($theme!=''){				Mage::getSingleton('core/session')->setCustomizeTheme($theme);			}		}				$this->_redirectUrl($url);    }}